var successResponse = function (req, res) {
    res.sendStatus(200);
};
var errorResponse = function (req, res) {
    res.sendStatus(400);
};

exports.Routes = [
    // Test multiple http methods for the same route config
    { path: '/list/:listName/items', httpMethod: ['OPTIONS', 'POST'], middleware: successResponse },
    { path: '/list/:listName/items/:id', httpMethod: 'GET', middleware: successResponse },
    { path: '/list/:listName/items/:id', httpMethod: 'PUT', middleware: errorResponse },
    { path: '/list/:listName/items/:id', httpMethod: 'DELETE', middleware: successResponse },

    // Same path and method as a route used by api-package1: it should trigger an error
    { path: '/open', httpMethod: 'POST', middleware: successResponse }
];